class Solution:
    def getLen(self, s):
        l = 1
        num = 0
        for c in s[1:]:
            if c == '(':
                l += 1
            else:
                l -= 1
            num += 1
            if l == 0:
                return num
        return -1
    def longestValidParentheses1(self, s):
        jump = 0
        maxN = 0
        l = 0
        i = 0
        while i < len(s):
            if s[i] == '(':
                jump = self.getLen(s[i:])
                if jump == -1:
                    if l >maxN:
                        maxN = l
                    l = 0
                    i += 1
                    continue
                i += jump
                l += jump + 1
            else:
                if l > maxN:
                    maxN = l
                l = 0
            i += 1
            if i == len(s):
                if l > maxN:
                    maxN = l
        return maxN
    def longestValidParentheses(self, s):
        if len(s) < 2:
            return len(s)
        stack = []
        listA = []
        for i in range(len(s)):
            c = s[i]
            if s[i] =='(':
                stack.append(i+1)
            else:
                if not stack:
                    listA.append(i+1)
                else:
                    stack.pop()
        stack.extend(listA)
        stack.append(0)
        stack.sort()
        stack.append(len(s)+1)
        print(stack)
        listB = []
        for i in range(1, len(stack)):
            listB.append(stack[i]-stack[i-1]-1)
        listB.sort()
        return listB[-1]

                    





S = Solution()
print(S.longestValidParentheses("(()"))





